mapper.select:
SELECT a.id, a.title, a.avatar, a.is_original, a.is_stick, a.read_type, a.is_publish, a.create_time, (select name from b_category where id = a.category_id) AS categoryName, (select nickname from b_user where id = a.user_id) AS nickname, (SELECT GROUP_CONCAT(t.NAME SEPARATOR ',') FROM b_tags t JOIN b_article_tag atg ON t.id = atg.tag_id WHERE atg.article_id = a.id) AS tagNames FROM b_article a WHERE 1 = 1
AND a.title LIKE CONCAT('%',#{title},'%')
AND a.category_id=#{categoryId}
AND a.is_publish=#{isPublish}
AND EXISTS (SELECT 1 FROM b_article_tag atg JOIN b_tags t ON atg.tag_id = t.id WHERE atg.article_id = a.id AND t.id=#{tagId})
GROUP BY a.id ORDER BY a.is_stick DESC, a.create_time DESC

mapper.update:
update b_article
set is_stick = #{article.isStick}
where id = #{article.id}

mapper.select:
SELECT a.*, u.nickname AS username, b.NAME AS categoryName, b.id AS categoryId FROM b_article a left JOIN b_category b ON a.category_id = b.id left JOIN b_user u ON a.user_id = u.id where a.user_id =#{userId}
and a.is_publish = #{type}
order by a.create_time desc
